import React, { useState, useEffect } from 'react';
import './App.css';
import MovieList from './MovieList';
import MovieHeading from './MovieHeading';
import SearchMovie from './SearchMovie';
import FavoriteMovie from './FavoriteMovie';
import RemoveMovie from './RemoveMovie';


function App() {

  const [movies, setMovies] = useState([])
  const [favorites, setFavorites] = useState([])
  const [searchTerm, setSearchTerm] = useState("")


  async function getMovies(searchTerm) {

    const url = `http://www.omdbapi.com/?s=${searchTerm}&apikey=263d22d8`;

    const res = await fetch(url)
    const data = await res.json();

    console.log(data.Search)
    if (data.Search) {
      setMovies(data.Search)
    }
  }


  useEffect(() => {
    getMovies(searchTerm)
  }, [searchTerm])


  useEffect(() => {
    const moviesFav = JSON.parse(
      localStorage.getItem("fav-items"))
    setFavorites(moviesFav)
  }, [])


  function saveToLs(items) {
    localStorage.setItem("fav-items", JSON.stringify(items))
  }


  function addFavoriteMovie(movie) {
    const newFavoriteList = [...favorites, movie]
    setFavorites(newFavoriteList)
    saveToLs(newFavoriteList)
  }



  function RemoveMovieFunc(movie) {
    const newFavoriteList = favorites.filter((favorite) => favorite.imdbID !== movie.imdbID)
    setFavorites(newFavoriteList);
    saveToLs(newFavoriteList)
  }


  return (
    <div className='container'>

      <div className='row'>
        <MovieHeading MovieHeading="Movies" />
        <SearchMovie searchTerm={searchTerm} setSearchTerm={setSearchTerm} />
      </div>
      <div>
        <MovieList
          movies={movies}
          handleFavoritesMovie={addFavoriteMovie}
          FavoriteMovie={FavoriteMovie} />
      </div>

      <div className='row'>
        <MovieHeading MovieHeading="Favorites" />
      </div>
      <div>
        <MovieList
          movies={favorites}
          handleFavoritesMovie={RemoveMovieFunc}
          FavoriteMovie={RemoveMovie} />
      </div>
    </div>
  );
}

export default App;
