import React, { useState, useEffect } from 'react';
import './App.css';
import MovieList from './MovieList';
import MovieHeading from './MovieHeading';
import SearchMovie from './SearchMovie';
import FavoriteMovie from './FavoriteMovie';



function App() {

  const [movies, setMovies] = useState([])
  const [favorite, setFavorite] = useState([])
  const [searchTerm, setSearchTerm] = useState("")


  async function getMovies(searchTerm) {

    const url = `http://www.omdbapi.com/?s=${searchTerm}&apikey=263d22d8`;

    const res = await fetch(url)
    const data = await res.json();

    if (data.Search) {
      setMovies(data.Search)
    }
  }


  useEffect(() => {
    getMovies(searchTerm)
  }, [searchTerm])


  return (
    <div className='container'>

      <header className='header'>
        <MovieHeading MovieHeading="Movies" />
        <SearchMovie searchTerm={searchTerm} setSearchTerm={setSearchTerm} />
      </header>

      <MovieList movies={movies} FavoriteMovie={FavoriteMovie} />


      <footer className='footer'>
        <MovieHeading MovieHeading="Favorites" />
        <SearchMovie searchTerm={searchTerm} setSearchTerm={setSearchTerm} />
      </footer>

    </div>
  );
}

export default App;
